import React, { useState } from "react";

export default function ToDoItem(props) {
  const [style, setStyle] = useState({
    "text-decoration": "",
  });

  function handleClick() {
    console.log("clicked");
    const { "text-decoration": textDeco } = style;
    style.textDeco === "line-through"
      ? setStyle({ "text-decoration": "" })
      : setStyle({ "text-decoration": "line-through" });
  }

  return (
    <li style={style} onClick={handleClick}>
      {props.text}
    </li>
  );
}
